import React from "react";
import { cn } from "@/lib/utils";
import { X, AlertTriangle, CheckCircle, Info, AlertCircle } from "lucide-react";
import { ZohoButton } from "./zoho-button";

interface ZohoModalProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  size?: "sm" | "md" | "lg" | "xl" | "full";
  children: React.ReactNode;
  footer?: React.ReactNode;
  closeOnBackdrop?: boolean;
  className?: string;
}

export function ZohoModal({
  isOpen,
  onClose,
  title,
  size = "md",
  children,
  footer,
  closeOnBackdrop = true,
  className,
}: ZohoModalProps) {
  React.useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  React.useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape" && isOpen) {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  const sizeClasses = {
    sm: "max-w-md",
    md: "max-w-lg",
    lg: "max-w-2xl",
    xl: "max-w-4xl",
    full: "max-w-full mx-4",
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black bg-opacity-50 backdrop-blur-sm"
        onClick={closeOnBackdrop ? onClose : undefined}
        data-testid="modal-backdrop"
      />

      {/* Modal */}
      <div
        className={cn(
          "relative w-full bg-white dark:bg-gray-800 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700 max-h-[90vh] overflow-hidden",
          sizeClasses[size],
          className
        )}
        data-testid="modal-container"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
          {title && (
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              {title}
            </h3>
          )}
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
            data-testid="modal-close-button"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
          {children}
        </div>

        {/* Footer */}
        {footer && (
          <div className="flex items-center justify-end gap-3 p-6 bg-gray-50 dark:bg-gray-700 border-t border-gray-200 dark:border-gray-600">
            {footer}
          </div>
        )}
      </div>
    </div>
  );
}

// Confirmation Dialog
interface ZohoConfirmDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  title: string;
  message: string;
  type?: "default" | "warning" | "danger";
  confirmText?: string;
  cancelText?: string;
  loading?: boolean;
}

export function ZohoConfirmDialog({
  isOpen,
  onClose,
  onConfirm,
  title,
  message,
  type = "default",
  confirmText = "Confirm",
  cancelText = "Cancel",
  loading = false,
}: ZohoConfirmDialogProps) {
  const iconMap = {
    default: <Info className="w-6 h-6 text-blue-600" />,
    warning: <AlertTriangle className="w-6 h-6 text-yellow-600" />,
    danger: <AlertCircle className="w-6 h-6 text-red-600" />,
  };

  const buttonVariant = {
    default: "primary" as const,
    warning: "primary" as const,
    danger: "danger" as const,
  };

  return (
    <ZohoModal
      isOpen={isOpen}
      onClose={onClose}
      size="sm"
      closeOnBackdrop={!loading}
      footer={
        <>
          <ZohoButton
            variant="outline"
            onClick={onClose}
            disabled={loading}
            data-testid="confirm-dialog-cancel"
          >
            {cancelText}
          </ZohoButton>
          <ZohoButton
            variant={buttonVariant[type]}
            onClick={onConfirm}
            loading={loading}
            data-testid="confirm-dialog-confirm"
          >
            {confirmText}
          </ZohoButton>
        </>
      }
    >
      <div className="flex items-start gap-4" data-testid="confirm-dialog-content">
        {iconMap[type]}
        <div>
          <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
            {title}
          </h4>
          <p className="text-gray-600 dark:text-gray-400">
            {message}
          </p>
        </div>
      </div>
    </ZohoModal>
  );
}

// Alert Dialog
interface ZohoAlertDialogProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  message: string;
  type?: "success" | "error" | "warning" | "info";
  buttonText?: string;
}

export function ZohoAlertDialog({
  isOpen,
  onClose,
  title,
  message,
  type = "info",
  buttonText = "OK",
}: ZohoAlertDialogProps) {
  const iconMap = {
    success: <CheckCircle className="w-6 h-6 text-green-600" />,
    error: <AlertCircle className="w-6 h-6 text-red-600" />,
    warning: <AlertTriangle className="w-6 h-6 text-yellow-600" />,
    info: <Info className="w-6 h-6 text-blue-600" />,
  };

  return (
    <ZohoModal
      isOpen={isOpen}
      onClose={onClose}
      size="sm"
      footer={
        <ZohoButton
          variant="primary"
          onClick={onClose}
          data-testid="alert-dialog-ok"
        >
          {buttonText}
        </ZohoButton>
      }
    >
      <div className="flex items-start gap-4" data-testid="alert-dialog-content">
        {iconMap[type]}
        <div>
          <h4 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
            {title}
          </h4>
          <p className="text-gray-600 dark:text-gray-400">
            {message}
          </p>
        </div>
      </div>
    </ZohoModal>
  );
}

// Drawer Component
interface ZohoDrawerProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  position?: "left" | "right";
  size?: "sm" | "md" | "lg";
  children: React.ReactNode;
  footer?: React.ReactNode;
  className?: string;
}

export function ZohoDrawer({
  isOpen,
  onClose,
  title,
  position = "right",
  size = "md",
  children,
  footer,
  className,
}: ZohoDrawerProps) {
  React.useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  React.useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape" && isOpen) {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [isOpen, onClose]);

  const sizeClasses = {
    sm: "w-80",
    md: "w-96",
    lg: "w-[500px]",
  };

  const positionClasses = {
    left: {
      container: "justify-start",
      panel: "animate-slide-in-left",
      closed: "-translate-x-full",
    },
    right: {
      container: "justify-end",
      panel: "animate-slide-in-right",
      closed: "translate-x-full",
    },
  };

  return (
    <div
      className={cn(
        "fixed inset-0 z-50 flex",
        positionClasses[position].container,
        isOpen ? "visible" : "invisible"
      )}
    >
      {/* Backdrop */}
      <div
        className={cn(
          "absolute inset-0 bg-black transition-opacity duration-300",
          isOpen ? "bg-opacity-50" : "bg-opacity-0"
        )}
        onClick={onClose}
        data-testid="drawer-backdrop"
      />

      {/* Drawer */}
      <div
        className={cn(
          "relative h-full bg-white dark:bg-gray-800 shadow-xl border-l border-gray-200 dark:border-gray-700 flex flex-col transition-transform duration-300",
          sizeClasses[size],
          isOpen ? "translate-x-0" : positionClasses[position].closed,
          className
        )}
        data-testid="drawer-container"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
          {title && (
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              {title}
            </h3>
          )}
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
            data-testid="drawer-close-button"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 p-6 overflow-y-auto">
          {children}
        </div>

        {/* Footer */}
        {footer && (
          <div className="flex items-center justify-end gap-3 p-6 bg-gray-50 dark:bg-gray-700 border-t border-gray-200 dark:border-gray-600">
            {footer}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/ui/zoho-notificati.content:
import React from "react";
import { cn } from "@/lib/utils";
import { X, CheckCircle, AlertCircle, AlertTriangle, Info, Bell } from "lucide-react";

interface ZohoToastProps {
  id: string;
  type: "success" | "error" | "warning" | "info";
  title: string;
  message?: string;
  duration?: number;
  onClose: (id: string) => void;
  action?: {
    label: string;
    onClick: () => void;
  };
}

export function ZohoToast({
  id,
  type,
  title,
  message,
  duration = 5000,
  onClose,
  action,
}: ZohoToastProps) {
  const [isVisible, setIsVisible] = React.useState(true);
  const [isExiting, setIsExiting] = React.useState(false);

  React.useEffect(() => {
    if (duration > 0) {
      const timer = setTimeout(() => {
        setIsExiting(true);
        setTimeout(() => onClose(id), 300);
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [duration, id, onClose]);

  const iconMap = {
    success: <CheckCircle className="w-5 h-5 text-green-600" />,
    error: <AlertCircle className="w-5 h-5 text-red-600" />,
    warning: <AlertTriangle className="w-5 h-5 text-yellow-600" />,
    info: <Info className="w-5 h-5 text-blue-600" />,
  };

  const colorClasses = {
    success: "border-green-200 bg-green-50 dark:border-green-800 dark:bg-green-900/20",
    error: "border-red-200 bg-red-50 dark:border-red-800 dark:bg-red-900/20",
    warning: "border-yellow-200 bg-yellow-50 dark:border-yellow-800 dark:bg-yellow-900/20",
    info: "border-blue-200 bg-blue-50 dark:border-blue-800 dark:bg-blue-900/20",
  };

  const handleClose = () => {
    setIsExiting(true);
    setTimeout(() => onClose(id), 300);
  };

  return (
    <div
      className={cn(
        "w-full max-w-sm border rounded-lg shadow-lg p-4 transition-all duration-300 transform",
        colorClasses[type],
        isExiting ? "translate-x-full opacity-0" : "translate-x-0 opacity-100"
      )}
      data-testid={`toast-${type}-${id}`}
    >
      <div className="flex items-start gap-3">
        {iconMap[type]}

        <div className="flex-1 min-w-0">
          <div className="text-sm font-medium text-gray-900 dark:text-white">
            {title}
          </div>
          {message && (
            <div className="text-sm text-gray-600 dark:text-gray-400 mt-1">
              {message}
            </div>
          )}
          {action && (
            <div className="mt-2">
              <button
                onClick={action.onClick}
                className="text-sm font-medium text-blue-600 hover:text-blue-500 dark:text-blue-400 dark:hover:text-blue-300"
                data-testid={`toast-action-${id}`}
              >
                {action.label}
              </button>
            </div>
          )}
        </div>

        <button
          onClick={handleClose}
          className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
          data-testid={`toast-close-${id}`}
        >
          <X className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}

// Toast Container
interface ZohoToastContainerProps {
  toasts: ZohoToastProps[];
  position?: "top-right" | "top-left" | "bottom-right" | "bottom-left" | "top-center" | "bottom-center";
}

export function ZohoToastContainer({ 
  toasts, 
  position = "top-right" 
}: ZohoToastContainerProps) {
  const positionClasses = {
    "top-right": "top-4 right-4",
    "top-left": "top-4 left-4",
    "bottom-right": "bottom-4 right-4",
    "bottom-left": "bottom-4 left-4",
    "top-center": "top-4 left-1/2 transform -translate-x-1/2",
    "bottom-center": "bottom-4 left-1/2 transform -translate-x-1/2",
  };

  return (
    <div
      className={cn(
        "fixed z-50 space-y-2",
        positionClasses[position]
      )}
      data-testid="toast-container"
    >
      {toasts.map((toast) => (
        <ZohoToast key={toast.id} {...toast} />
      ))}
    </div>
  );
}

// Banner Notification
interface ZohoBannerProps {
  type: "info" | "warning" | "error" | "success";
  title?: string;
  message: string;
  action?: {
    label: string;
    onClick: () => void;
  };
  dismissible?: boolean;
  onDismiss?: () => void;
  className?: string;
}

export function ZohoBanner({
  type,
  title,
  message,
  action,
  dismissible = true,
  onDismiss,
  className,
}: ZohoBannerProps) {
  const [isVisible, setIsVisible] = React.useState(true);

  const iconMap = {
    success: <CheckCircle className="w-5 h-5 text-green-600" />,
    error: <AlertCircle className="w-5 h-5 text-red-600" />,
    warning: <AlertTriangle className="w-5 h-5 text-yellow-600" />,
    info: <Info className="w-5 h-5 text-blue-600" />,
  };

  const colorClasses = {
    success: "border-green-200 bg-green-50 dark:border-green-800 dark:bg-green-900/20",
    error: "border-red-200 bg-red-50 dark:border-red-800 dark:bg-red-900/20",
    warning: "border-yellow-200 bg-yellow-50 dark:border-yellow-800 dark:bg-yellow-900/20",
    info: "border-blue-200 bg-blue-50 dark:border-blue-800 dark:bg-blue-900/20",
  };

  const handleDismiss = () => {
    setIsVisible(false);
    onDismiss?.();
  };

  if (!isVisible) return null;

  return (
    <div
      className={cn(
        "border rounded-lg p-4",
        colorClasses[type],
        className
      )}
      data-testid={`banner-${type}`}
    >
      <div className="flex items-start gap-3">
        {iconMap[type]}

        <div className="flex-1 min-w-0">
          {title && (
            <div className="text-sm font-medium text-gray-900 dark:text-white mb-1">
              {title}
            </div>
          )}
          <div className="text-sm text-gray-600 dark:text-gray-400">
            {message}
          </div>
          {action && (
            <div className="mt-2">
              <button
                onClick={action.onClick}
                className="text-sm font-medium text-blue-600 hover:text-blue-500 dark:text-blue-400 dark:hover:text-blue-300"
                data-testid="banner-action"
              >
                {action.label}
              </button>
            </div>
          )}
        </div>

        {dismissible && (
          <button
            onClick={handleDismiss}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
            data-testid="banner-dismiss"
          >
            <X className="w-4 h-4" />
          </button>
        )}
      </div>
    </div>
  );
}

// Notification Bell with Count
interface ZohoNotificationBellProps {
  count?: number;
  onClick?: () => void;
  className?: string;
}

export function ZohoNotificationBell({ 
  count = 0, 
  onClick,
  className 
}: ZohoNotificationBellProps) {
  return (
    <button
      onClick={onClick}
      className={cn(
        "relative p-2 text-gray-600 hover:text-gray-900 dark:text-gray-400 dark:hover:text-white transition-colors duration-200",
        className
      )}
      data-testid="notification-bell"
    >
      <Bell className="w-5 h-5" />
      {count > 0 && (
        <span className="absolute -top-1 -right-1 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-white bg-red-600 rounded-full">
          {count > 99 ? "99+" : count}
        </span>
      )}
    </button>
  );
}

// Progress Banner
interface ZohoProgressBannerProps {
  title: string;
  message?: string;
  progress: number; // 0-100
  showPercentage?: boolean;
  onCancel?: () => void;
  className?: string;
}

export function ZohoProgressBanner({
  title,
  message,
  progress,
  showPercentage = true,
  onCancel,
  className,
}: ZohoProgressBannerProps) {
  return (
    <div
      className={cn(
        "border border-blue-200 bg-blue-50 dark:border-blue-800 dark:bg-blue-900/20 rounded-lg p-4",
        className
      )}
      data-testid="progress-banner"
    >
      <div className="flex items-start justify-between gap-3">
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-2">
            <div className="text-sm font-medium text-gray-900 dark:text-white">
              {title}
            </div>
            {showPercentage && (
              <div className="text-sm text-gray-600 dark:text-gray-400">
                {Math.round(progress)}%
              </div>
            )}
          </div>

          {message && (
            <div className="text-sm text-gray-600 dark:text-gray-400 mb-3">
              {message}
            </div>
          )}

          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
            <div
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${Math.min(100, Math.max(0, progress))}%` }}
            />
          </div>
        </div>

        {onCancel && (
          <button
            onClick={onCancel}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
            data-testid="progress-banner-cancel"
          >
            <X className="w-4 h-4" />
          </button>
        )}
      </div>
    </div>
  );
}

