              >
                <div className="text-blue-600 dark:text-blue-400">
                  {feature.icon}
                </div>
                <div>
                  <div className="font-medium text-gray-900 dark:text-white mb-1">
                    {feature.title}
                  </div>
                  <div className="text-sm text-gray-600 dark:text-gray-400">
                    {feature.description}
                  </div>
                </div>
