}

interface TourStep {
  id: string;
  title: string;
  content: string;
  target: string; // CSS selector
  position?: "top" | "bottom" | "left" | "right";
  highlight?: boolean;
}

interface ZohoProductTourProps {
  isActive: boolean;
  steps: TourStep[];
  currentStep: number;
  onNext: () => void;
  onPrevious: () => void;
  onComplete: () => void;
  onSkip: () => void;
  className?: string;
}

export function ZohoProductTour({
  isActive,
  steps,
  currentStep,
  onNext,
  onPrevious,
  onComplete,
  onSkip,
  className,
}: ZohoProductTourProps) {
  const [targetElement, setTargetElement] = React.useState<HTMLElement | null>(null);
  const [tooltipPosition, setTooltipPosition] = React.useState({ top: 0, left: 0 });

  const currentStepData = steps[currentStep];
  const isLastStep = currentStep === steps.length - 1;
  const isFirstStep = currentStep === 0;

  React.useEffect(() => {
    if (!isActive || !currentStepData) return;

    const element = document.querySelector(currentStepData.target) as HTMLElement;
    if (element) {
      setTargetElement(element);

      // Scroll element into view
      element.scrollIntoView({ behavior: 'smooth', block: 'center' });

      // Calculate tooltip position
      const rect = element.getBoundingClientRect();
      const position = currentStepData.position || 'bottom';

      let top = 0;
      let left = 0;

      switch (position) {
        case 'top':
          top = rect.top - 10;
          left = rect.left + rect.width / 2;
          break;
        case 'bottom':
          top = rect.bottom + 10;
          left = rect.left + rect.width / 2;
          break;
        case 'left':
          top = rect.top + rect.height / 2;
          left = rect.left - 10;
          break;
        case 'right':
          top = rect.top + rect.height / 2;
          left = rect.right + 10;
          break;
      }

      setTooltipPosition({ top, left });

      // Add highlight if needed
      if (currentStepData.highlight) {
        element.classList.add('tour-highlight');
      }
    }

    return () => {
      // Remove highlight
      if (element && currentStepData.highlight) {
        element.classList.remove('tour-highlight');
      }
    };
  }, [isActive, currentStep, currentStepData]);

  if (!isActive || !currentStepData || !targetElement) return null;

  return (
    <>
      {/* Overlay */}
      <div className="fixed inset-0 bg-black bg-opacity-50 z-40" />

      {/* Tooltip */}
      <div
        className={cn(
          "fixed z-50 w-80 bg-white dark:bg-gray-800 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700 p-6 transform -translate-x-1/2 -translate-y-1/2",
          className
        )}
        style={{
          top: tooltipPosition.top,
          left: tooltipPosition.left,
        }}
        data-testid="product-tour-tooltip"
      >
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center gap-2">
            <Lightbulb className="w-5 h-5 text-yellow-500" />
            <span className="text-sm font-medium text-gray-600 dark:text-gray-400">
              Step {currentStep + 1} of {steps.length}
            </span>
          </div>
          <button
            onClick={onSkip}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
            data-testid="tour-skip-button"
          >
            <X className="w-4 h-4" />
          </button>
        </div>

        {/* Content */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
            {currentStepData.title}
          </h3>
          <p className="text-gray-600 dark:text-gray-400">
            {currentStepData.content}
          </p>
        </div>

        {/* Progress */}
        <div className="mb-4">
          <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mb-2">
            <span>Progress</span>
            <span>{Math.round(((currentStep + 1) / steps.length) * 100)}%</span>
          </div>
          <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
            <div
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${((currentStep + 1) / steps.length) * 100}%` }}
            />
          </div>
        </div>

        {/* Actions */}
        <div className="flex items-center justify-between">
          <ZohoButton
            variant="ghost"
            size="sm"
            onClick={onPrevious}
            disabled={isFirstStep}
            data-testid="tour-previous-button"
          >
            Previous
          </ZohoButton>

          <div className="flex gap-2">
            <ZohoButton
              variant="outline"
              size="sm"
              onClick={onSkip}
              data-testid="tour-skip-all-button"
            >
              Skip Tour
            </ZohoButton>

            {isLastStep ? (
              <ZohoButton
                variant="primary"
                size="sm"
                onClick={onComplete}
                data-testid="tour-complete-button"
              >
                Complete
              </ZohoButton>
            ) : (
              <ZohoButton
                variant="primary"
                size="sm"
                onClick={onNext}
                data-testid="tour-next-button"
              >
                Next
              </ZohoButton>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

// Empty State Component
interface ZohoEmptyStateProps {
