import React from "react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { 
  Plus, 
  Search, 
  Filter, 
  Download, 
  Upload, 
  Settings, 
  MoreHorizontal,
  Trash2,
  Edit,
  Copy,
  Share2,
  RefreshCw,
  Save,
  X,
  Check
} from "lucide-react";

interface ZohoButtonProps {
  variant?: "primary" | "secondary" | "danger" | "success" | "outline" | "ghost";
  size?: "sm" | "md" | "lg";
  icon?: "plus" | "search" | "filter" | "download" | "upload" | "settings" | "more" | "trash" | "edit" | "copy" | "share" | "refresh" | "save" | "close" | "check";
  children?: React.ReactNode;
  onClick?: () => void;
  disabled?: boolean;
  loading?: boolean;
  className?: string;
  fullWidth?: boolean;
}

const iconMap = {
  plus: Plus,
  search: Search,
  filter: Filter,
  download: Download,
  upload: Upload,
  settings: Settings,
  more: MoreHorizontal,
  trash: Trash2,
  edit: Edit,
  copy: Copy,
  share: Share2,
  refresh: RefreshCw,
  save: Save,
  close: X,
  check: Check,
};

export function ZohoButton({
  variant = "primary",
  size = "md",
  icon,
  children,
  onClick,
  disabled = false,
  loading = false,
  className,
  fullWidth = false,
  ...props
}: ZohoButtonProps) {
  const IconComponent = icon ? iconMap[icon] : null;

  const baseClasses = "relative inline-flex items-center justify-center font-medium transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed";

  const variantClasses = {
    primary: "bg-blue-600 hover:bg-blue-700 text-white border border-blue-600 focus:ring-blue-500 shadow-sm",
    secondary: "bg-gray-100 hover:bg-gray-200 text-gray-900 border border-gray-300 focus:ring-gray-500 dark:bg-gray-700 dark:hover:bg-gray-600 dark:text-white dark:border-gray-600",
    danger: "bg-red-600 hover:bg-red-700 text-white border border-red-600 focus:ring-red-500 shadow-sm",
    success: "bg-green-600 hover:bg-green-700 text-white border border-green-600 focus:ring-green-500 shadow-sm",
    outline: "bg-transparent hover:bg-gray-50 text-gray-700 border border-gray-300 focus:ring-blue-500 dark:hover:bg-gray-700 dark:text-gray-300 dark:border-gray-600",
    ghost: "bg-transparent hover:bg-gray-100 text-gray-700 border-0 focus:ring-blue-500 dark:hover:bg-gray-700 dark:text-gray-300",
  };

  const sizeClasses = {
    sm: "px-3 py-1.5 text-xs rounded-md gap-1.5",
    md: "px-4 py-2 text-sm rounded-md gap-2",
    lg: "px-6 py-3 text-base rounded-lg gap-2.5",
  };

  const iconSizes = {
    sm: "w-3 h-3",
    md: "w-4 h-4", 
    lg: "w-5 h-5",
  };

  return (
    <Button
      className={cn(
        baseClasses,
        variantClasses[variant],
        sizeClasses[size],
        fullWidth && "w-full",
        loading && "cursor-wait",
        className
      )}
      onClick={onClick}
      disabled={disabled || loading}
      data-testid={`button-${variant}-${children?.toString().toLowerCase().replace(/\s+/g, '-') || 'action'}`}
      {...props}
    >
      {loading && (
        <div className="absolute inset-0 flex items-center justify-center">
          <RefreshCw className={cn(iconSizes[size], "animate-spin")} />
        </div>
      )}

      <div className={cn("flex items-center gap-2", loading && "opacity-0")}>
        {IconComponent && (
          <IconComponent className={iconSizes[size]} />
        )}
        {children}
      </div>
    </Button>
  );
}

// Toolbar component
interface ZohoToolbarProps {
  children: React.ReactNode;
  className?: string;
}

export function ZohoToolbar({ children, className }: ZohoToolbarProps) {
  return (
    <div 
      className={cn(
        "flex items-center gap-2 p-4 bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700",
        className
      )}
      data-testid="toolbar-container"
    >
      {children}
    </div>
  );
}

// Action Menu Component
interface ZohoActionMenuProps {
  items: Array<{
    label: string;
    icon?: keyof typeof iconMap;
    onClick: () => void;
    disabled?: boolean;
    variant?: "default" | "danger";
  }>;
  trigger?: React.ReactNode;
  className?: string;
}

export function ZohoActionMenu({ items, trigger, className }: ZohoActionMenuProps) {
  const [isOpen, setIsOpen] = React.useState(false);

  return (
    <div className={cn("relative", className)}>
      <div
        onClick={() => setIsOpen(!isOpen)}
        className="cursor-pointer"
        data-testid="action-menu-trigger"
      >
        {trigger || <ZohoButton variant="ghost" icon="more" size="sm" />}
      </div>

      {isOpen && (
        <>
          <div 
            className="fixed inset-0 z-10" 
            onClick={() => setIsOpen(false)}
          />
          <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-md shadow-lg border border-gray-200 dark:border-gray-700 z-20">
            <div className="py-1">
              {items.map((item, index) => {
                const IconComponent = item.icon ? iconMap[item.icon] : null;
                return (
                  <button
                    key={index}
                    onClick={() => {
                      item.onClick();
                      setIsOpen(false);
                    }}
                    disabled={item.disabled}
                    className={cn(
                      "flex items-center gap-2 w-full px-4 py-2 text-sm text-left hover:bg-gray-100 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed",
                      item.variant === "danger" && "text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20"
                    )}
                    data-testid={`action-menu-item-${item.label.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    {IconComponent && <IconComponent className="w-4 h-4" />}
                    {item.label}
                  </button>
                );
              })}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

import React from "react";
import { cn } from "@/lib/utils";
import { BarChart3, TrendingUp, TrendingDown, Users, DollarSign, Package, Activity } from "lucide-react";

interface ZohoCardProps {
  title: string;
  value?: string | number;
  subtitle?: string;
  trend?: {
    value: number;
    direction: "up" | "down";
    period?: string;
  };
  icon?: "chart" | "trending-up" | "trending-down" | "users" | "dollar" | "package" | "activity";
  variant?: "default" | "primary" | "success" | "warning" | "danger";
  size?: "sm" | "md" | "lg";
  children?: React.ReactNode;
  className?: string;
  loading?: boolean;
}

const iconMap = {
  chart: BarChart3,
  "trending-up": TrendingUp,
  "trending-down": TrendingDown,
  users: Users,
  dollar: DollarSign,
  package: Package,
  activity: Activity,
};

const variantColors = {
  default: "bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700",
  primary: "bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800",
  success: "bg-green-50 dark:bg-green-900/20 border-green-200 dark:border-green-800",
  warning: "bg-yellow-50 dark:bg-yellow-900/20 border-yellow-200 dark:border-yellow-800",
  danger: "bg-red-50 dark:bg-red-900/20 border-red-200 dark:border-red-800",
};

const iconColors = {
  default: "text-gray-600 dark:text-gray-400",
  primary: "text-blue-600 dark:text-blue-400",
  success: "text-green-600 dark:text-green-400",
  warning: "text-yellow-600 dark:text-yellow-400",
  danger: "text-red-600 dark:text-red-400",
};

export function ZohoCard({
  title,
  value,
  subtitle,
  trend,
  icon,
  variant = "default",
  size = "md",
  children,
  className,
  loading = false,
}: ZohoCardProps) {
  const IconComponent = icon ? iconMap[icon] : null;

  const sizeClasses = {
    sm: "p-4",
    md: "p-6",
    lg: "p-8",
  };

  if (loading) {
    return (
      <div className={cn(
        "rounded-lg border",
        variantColors[variant],
        sizeClasses[size],
        "animate-pulse",
        className
      )}>
        <div className="flex items-center justify-between mb-4">
          <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-1/2"></div>
          <div className="h-8 w-8 bg-gray-300 dark:bg-gray-600 rounded"></div>
        </div>
        <div className="h-8 bg-gray-300 dark:bg-gray-600 rounded w-3/4 mb-2"></div>
        <div className="h-4 bg-gray-300 dark:bg-gray-600 rounded w-1/3"></div>
      </div>
    );
  }

  return (
    <div 
      className={cn(
        "rounded-lg border shadow-sm hover:shadow-md transition-shadow duration-200",
        variantColors[variant],
        sizeClasses[size],
        className
      )}
      data-testid={`card-${title.toLowerCase().replace(/\s+/g, '-')}`}
    >
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-sm font-medium text-gray-600 dark:text-gray-400">
          {title}
        </h3>
        {IconComponent && (
          <IconComponent className={cn("w-5 h-5", iconColors[variant])} />
        )}
      </div>

      {value && (
        <div className="mb-2">
          <div className="text-2xl font-bold text-gray-900 dark:text-white">
            {value}
          </div>
        </div>
      )}

      {(subtitle || trend) && (
        <div className="flex items-center justify-between text-sm">
          {subtitle && (
            <span className="text-gray-500 dark:text-gray-400">
              {subtitle}
            </span>
          )}
          {trend && (
            <div className={cn(
              "flex items-center gap-1",
              trend.direction === "up" ? "text-green-600 dark:text-green-400" : "text-red-600 dark:text-red-400"
            )}>
              {trend.direction === "up" ? (
                <TrendingUp className="w-3 h-3" />
              ) : (
                <TrendingDown className="w-3 h-3" />
              )}
              <span className="font-medium">{trend.value}%</span>
              {trend.period && (
                <span className="text-gray-500 dark:text-gray-400">
                  {trend.period}
                </span>
              )}
            </div>
          )}
        </div>
      )}

      {children && (
        <div className="mt-4">
          {children}
        </div>
      )}
    </div>
  );
}

// Dashboard Grid Layout
interface ZohoDashboardGridProps {
  children: React.ReactNode;
  columns?: 1 | 2 | 3 | 4;
  gap?: "sm" | "md" | "lg";
  className?: string;
}

export function ZohoDashboardGrid({ 
  children, 
  columns = 3,
  gap = "md",
  className 
}: ZohoDashboardGridProps) {
  const gridCols = {
    1: "grid-cols-1",
    2: "grid-cols-1 md:grid-cols-2",
    3: "grid-cols-1 md:grid-cols-2 lg:grid-cols-3",
    4: "grid-cols-1 md:grid-cols-2 lg:grid-cols-4",
  };

  const gridGap = {
    sm: "gap-4",
    md: "gap-6",
    lg: "gap-8",
  };

  return (
    <div 
      className={cn(
        "grid",
        gridCols[columns],
        gridGap[gap],
        className
      )}
      data-testid="dashboard-grid"
    >
      {children}
    </div>
