interface ZohoFileUploadProps {
  label?: string;
  error?: string;
  hint?: string;
  required?: boolean;
  accept?: string;
  multiple?: boolean;
  maxSize?: number; // in MB
  onFileSelect?: (files: FileList | null) => void;
  loading?: boolean;
  className?: string;
}

export function ZohoFileUpload({
  label,
  error,
  hint,
  required,
  accept,
  multiple,
  maxSize,
  onFileSelect,
  loading,
  className,
}: ZohoFileUploadProps) {
  const [dragActive, setDragActive] = React.useState(false);
  const [selectedFiles, setSelectedFiles] = React.useState<File[]>([]);
  const inputRef = React.useRef<HTMLInputElement>(null);

  const handleFiles = (files: FileList | null) => {
    if (!files) return;

    const fileArray = Array.from(files);

    // Validate file size
    if (maxSize) {
      const oversizedFiles = fileArray.filter(file => file.size > maxSize * 1024 * 1024);
      if (oversizedFiles.length > 0) {
        alert(`Files must be smaller than ${maxSize}MB`);
        return;
      }
    }

    setSelectedFiles(fileArray);
    onFileSelect?.(files);
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    handleFiles(e.dataTransfer.files);
  };

  const removeFile = (index: number) => {
    const newFiles = selectedFiles.filter((_, i) => i !== index);
    setSelectedFiles(newFiles);

    // Create new FileList-like object
    const dt = new DataTransfer();
    newFiles.forEach(file => dt.items.add(file));
    onFileSelect?.(dt.files);
  };

  return (
    <div className="w-full">
      {label && (
        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
          {label}
          {required && <span className="text-red-500 ml-1">*</span>}
        </label>
      )}

      <div
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        className={cn(
          "border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center hover:border-gray-400 dark:hover:border-gray-500 transition-colors duration-200",
          dragActive && "border-blue-500 bg-blue-50 dark:bg-blue-900/20",
          error && "border-red-500",
          loading && "opacity-50 cursor-not-allowed",
          className
        )}
        data-testid="file-upload-zone"
      >
        <input
          ref={inputRef}
          type="file"
          accept={accept}
          multiple={multiple}
          onChange={(e) => handleFiles(e.target.files)}
          className="hidden"
          disabled={loading}
          data-testid="file-upload-input"
        />

        <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
        <div className="text-gray-600 dark:text-gray-400">
          <button
            type="button"
            onClick={() => inputRef.current?.click()}
            disabled={loading}
            className="text-blue-600 hover:text-blue-700 dark:text-blue-400 dark:hover:text-blue-300 font-medium"
            data-testid="file-upload-button"
          >
            Click to upload
          </button>
          <span> or drag and drop</span>
        </div>
        {accept && (
          <div className="text-sm text-gray-500 dark:text-gray-400 mt-1">
            Accepted formats: {accept}
          </div>
        )}
        {maxSize && (
          <div className="text-sm text-gray-500 dark:text-gray-400">
            Max size: {maxSize}MB
          </div>
        )}
      </div>

      {/* Selected Files */}
      {selectedFiles.length > 0 && (
        <div className="mt-3 space-y-2">
          {selectedFiles.map((file, index) => (
            <div
              key={index}
              className="flex items-center justify-between p-2 bg-gray-50 dark:bg-gray-700 rounded-md"
              data-testid={`selected-file-${index}`}
            >
              <div className="flex items-center gap-2">
                <div className="text-sm text-gray-900 dark:text-white font-medium">
                  {file.name}
                </div>
                <div className="text-xs text-gray-500 dark:text-gray-400">
                  ({(file.size / 1024 / 1024).toFixed(2)} MB)
                </div>
              </div>
              <button
                type="button"
                onClick={() => removeFile(index)}
                className="text-gray-400 hover:text-red-600 dark:hover:text-red-400"
                data-testid={`remove-file-${index}`}
              >
                <X className="w-4 h-4" />
              </button>
            </div>
          ))}
        </div>
      )}

      {(error || hint) && (
        <div className="mt-1 text-sm">
          {error && (
            <div className="text-red-600 dark:text-red-400 flex items-center gap-1">
              <AlertCircle className="w-3 h-3" />
              {error}
            </div>
          )}
          {hint && !error && (
            <div className="text-gray-500 dark:text-gray-400">
              {hint}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

