interface ZohoFormRowProps {
  children: React.ReactNode;
  columns?: 1 | 2 | 3 | 4;
  gap?: "sm" | "md" | "lg";
  className?: string;
}

export function ZohoFormRow({ 
  children, 
  columns = 1,
  gap = "md",
  className 
}: ZohoFormRowProps) {
  const gridCols = {
    1: "grid-cols-1",
    2: "grid-cols-1 md:grid-cols-2",
    3: "grid-cols-1 md:grid-cols-3",
    4: "grid-cols-1 md:grid-cols-4",
  };

  const gridGap = {
    sm: "gap-4",
    md: "gap-6",
    lg: "gap-8",
  };

  return (
    <div 
      className={cn(
        "grid",
        gridCols[columns],
        gridGap[gap],
        className
      )}
      data-testid="form-row"
    >
      {children}
    </div>
  );
}

