// Progressive Loading
interface ZohoProgressiveLoadingProps {
  steps: Array<{
    id: string;
    title: string;
    completed: boolean;
  }>;
  currentStep?: string;
  className?: string;
}

export function ZohoProgressiveLoading({
  steps,
  currentStep,
  className,
}: ZohoProgressiveLoadingProps) {
  return (
    <div className={cn("space-y-4", className)}>
      {steps.map((step) => {
        const isActive = step.id === currentStep;
        const isCompleted = step.completed;

        return (
          <div
            key={step.id}
            className="flex items-center gap-3"
            data-testid={`loading-step-${step.id}`}
          >
            <div className={cn(
              "w-6 h-6 rounded-full flex items-center justify-center border-2",
              isCompleted && "bg-green-600 border-green-600 text-white",
              isActive && !isCompleted && "border-blue-600 text-blue-600",
              !isActive && !isCompleted && "border-gray-300 text-gray-300"
            )}>
              {isCompleted ? (
                <span className="text-xs">âœ“</span>
              ) : isActive ? (
                <ZohoSpinner size="sm" variant="primary" />
              ) : (
                <span className="w-2 h-2 bg-current rounded-full" />
              )}
            </div>

            <div className={cn(
              "text-sm",
              isCompleted && "text-green-600 dark:text-green-400",
              isActive && !isCompleted && "text-blue-600 dark:text-blue-400 font-medium",
              !isActive && !isCompleted && "text-gray-500 dark:text-gray-400"
            )}>
              {step.title}
            </div>
          </div>
        );
      })}
    </div>
  );
}

import React from "react";
import { cn } from "@/lib/utils";
import { X, AlertTriangle, CheckCircle, Info, AlertCircle } from "lucide-react";
import { ZohoButton } from "./zoho-button";

interface ZohoModalProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  size?: "sm" | "md" | "lg" | "xl" | "full";
  children: React.ReactNode;
  footer?: React.ReactNode;
  closeOnBackdrop?: boolean;
  className?: string;
}

export function ZohoModal({
  isOpen,
  onClose,
  title,
  size = "md",
  children,
  footer,
  closeOnBackdrop = true,
  className,
}: ZohoModalProps) {
  React.useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "unset";
    }

    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  React.useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape" && isOpen) {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  const sizeClasses = {
    sm: "max-w-md",
    md: "max-w-lg",
    lg: "max-w-2xl",
    xl: "max-w-4xl",
    full: "max-w-full mx-4",
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black bg-opacity-50 backdrop-blur-sm"
        onClick={closeOnBackdrop ? onClose : undefined}
        data-testid="modal-backdrop"
      />

      {/* Modal */}
      <div
        className={cn(
          "relative w-full bg-white dark:bg-gray-800 rounded-lg shadow-xl border border-gray-200 dark:border-gray-700 max-h-[90vh] overflow-hidden",
          sizeClasses[size],
          className
        )}
        data-testid="modal-container"
      >
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
          {title && (
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              {title}
            </h3>
          )}
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors duration-200"
            data-testid="modal-close-button"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
          {children}
        </div>

        {/* Footer */}
        {footer && (
          <div className="flex items-center justify-end gap-3 p-6 bg-gray-50 dark:bg-gray-700 border-t border-gray-200 dark:border-gray-600">
            {footer}
          </div>
        )}
      </div>
    </div>
  );
}

