import React from "react";
import { cn } from "@/lib/utils";
import { 
  ChevronUp, 
  ChevronDown, 
  MoreHorizontal, 
  Search,
  Filter,
  Download,
  ArrowUpDown,
  CheckSquare,
  Square
} from "lucide-react";
import { ZohoButton } from "./zoho-button";

interface Column {
  key: string;
  title: string;
  sortable?: boolean;
  filterable?: boolean;
  width?: string;
  render?: (value: any, record: any) => React.ReactNode;
}

interface ZohoTableProps {
  data: Array<Record<string, any>>;
  columns: Column[];
  loading?: boolean;
  selectable?: boolean;
  selectedRows?: string[];
  onRowSelect?: (selectedRowKeys: string[]) => void;
  onSort?: (key: string, direction: "asc" | "desc") => void;
  sortConfig?: { key: string; direction: "asc" | "desc" };
  pagination?: {
    current: number;
    pageSize: number;
    total: number;
    onChange: (page: number, pageSize: number) => void;
  };
  actions?: Array<{
    label: string;
    onClick: (record: any) => void;
    icon?: React.ReactNode;
  }>;
  className?: string;
}

export function ZohoTable({
  data,
  columns,
  loading = false,
  selectable = false,
  selectedRows = [],
  onRowSelect,
  onSort,
  sortConfig,
  pagination,
  actions,
  className,
}: ZohoTableProps) {
  const [searchTerm, setSearchTerm] = React.useState("");
  const [filters, setFilters] = React.useState<Record<string, string>>({});

  const handleSelectAll = () => {
    if (!onRowSelect) return;

    const allSelected = selectedRows.length === data.length;
    if (allSelected) {
      onRowSelect([]);
    } else {
      onRowSelect(data.map((_, index) => index.toString()));
    }
  };

  const handleRowSelect = (rowKey: string) => {
    if (!onRowSelect) return;

    const newSelection = selectedRows.includes(rowKey)
      ? selectedRows.filter(key => key !== rowKey)
      : [...selectedRows, rowKey];

    onRowSelect(newSelection);
  };

  const handleSort = (columnKey: string) => {
    if (!onSort) return;

    let direction: "asc" | "desc" = "asc";
    if (sortConfig?.key === columnKey && sortConfig.direction === "asc") {
      direction = "desc";
    }

    onSort(columnKey, direction);
  };

  const filteredData = React.useMemo(() => {
    return data.filter(item => {
      // Search filter
      if (searchTerm) {
        const searchValue = Object.values(item)
          .join(" ")
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
        if (!searchValue) return false;
      }

      // Column filters
      for (const [key, value] of Object.entries(filters)) {
        if (value && !String(item[key]).toLowerCase().includes(value.toLowerCase())) {
          return false;
        }
      }

      return true;
    });
  }, [data, searchTerm, filters]);

  if (loading) {
    return (
      <div className="w-full">
        <div className="border border-gray-200 dark:border-gray-700 rounded-lg">
          <div className="animate-pulse">
            {/* Table header skeleton */}
            <div className="border-b border-gray-200 dark:border-gray-700 p-4">
              <div className="flex space-x-4">
                {columns.map((_, index) => (
                  <div key={index} className="h-4 bg-gray-300 dark:bg-gray-600 rounded flex-1"></div>
                ))}
              </div>
            </div>
            {/* Table rows skeleton */}
            {[...Array(5)].map((_, index) => (
              <div key={index} className="border-b border-gray-200 dark:border-gray-700 p-4">
                <div className="flex space-x-4">
                  {columns.map((_, colIndex) => (
                    <div key={colIndex} className="h-4 bg-gray-300 dark:bg-gray-600 rounded flex-1"></div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={cn("w-full", className)}>
      {/* Table Toolbar */}
      <div className="flex items-center justify-between mb-4 p-4 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-t-lg">
        <div className="flex items-center gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              data-testid="table-search-input"
            />
          </div>
          <ZohoButton variant="outline" icon="filter" size="sm">
            Filter
          </ZohoButton>
        </div>

        <div className="flex items-center gap-2">
          <ZohoButton variant="outline" icon="download" size="sm">
            Export
          </ZohoButton>
          {selectedRows.length > 0 && (
            <span className="text-sm text-gray-600 dark:text-gray-400">
              {selectedRows.length} selected
            </span>
          )}
        </div>
      </div>

      {/* Table */}
      <div className="border border-gray-200 dark:border-gray-700 rounded-b-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead className="bg-gray-50 dark:bg-gray-700">
              <tr>
                {selectable && (
                  <th className="px-6 py-3 text-left">
                    <button
                      onClick={handleSelectAll}
                      className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                      data-testid="select-all-checkbox"
                    >
                      {selectedRows.length === data.length && data.length > 0 ? (
                        <CheckSquare className="w-4 h-4" />
                      ) : (
                        <Square className="w-4 h-4" />
                      )}
                    </button>
                  </th>
                )}
                {columns.map((column) => (
                  <th
                    key={column.key}
                    className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider"
                    style={{ width: column.width }}
                  >
                    <div className="flex items-center gap-2">
                      <span>{column.title}</span>
                      {column.sortable && (
                        <button
                          onClick={() => handleSort(column.key)}
                          className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                          data-testid={`sort-${column.key}`}
                        >
                          {sortConfig?.key === column.key ? (
                            sortConfig.direction === "asc" ? (
                              <ChevronUp className="w-4 h-4" />
                            ) : (
                              <ChevronDown className="w-4 h-4" />
                            )
                          ) : (
                            <ArrowUpDown className="w-4 h-4" />
                          )}
                        </button>
                      )}
                    </div>
                  </th>
                ))}
                {actions && actions.length > 0 && (
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider">
                    Actions
                  </th>
                )}
              </tr>
            </thead>
            <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
              {filteredData.map((record, index) => (
                <tr 
                  key={index}
                  className="hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors duration-150"
                  data-testid={`table-row-${index}`}
                >
                  {selectable && (
                    <td className="px-6 py-4">
                      <button
                        onClick={() => handleRowSelect(index.toString())}
                        className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300"
                        data-testid={`select-row-${index}`}
                      >
                        {selectedRows.includes(index.toString()) ? (
                          <CheckSquare className="w-4 h-4" />
                        ) : (
                          <Square className="w-4 h-4" />
                        )}
                      </button>
                    </td>
                  )}
                  {columns.map((column) => (
                    <td key={column.key} className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                      {column.render 
                        ? column.render(record[column.key], record)
                        : record[column.key]
                      }
                    </td>
                  ))}
                  {actions && actions.length > 0 && (
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex items-center justify-end gap-2">
                        {actions.map((action, actionIndex) => (
                          <button
                            key={actionIndex}
                            onClick={() => action.onClick(record)}
                            className="text-blue-600 hover:text-blue-900 dark:text-blue-400 dark:hover:text-blue-300"
                            data-testid={`action-${action.label.toLowerCase().replace(/\s+/g, '-')}-${index}`}
                          >
                            {action.icon || action.label}
                          </button>
                        ))}
                      </div>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {pagination && (
          <div className="px-6 py-3 bg-gray-50 dark:bg-gray-700 border-t border-gray-200 dark:border-gray-600">
            <div className="flex items-center justify-between">
              <div className="text-sm text-gray-500 dark:text-gray-400">
                Showing {((pagination.current - 1) * pagination.pageSize) + 1} to {Math.min(pagination.current * pagination.pageSize, pagination.total)} of {pagination.total} results
              </div>
              <div className="flex items-center gap-2">
                <ZohoButton
                  variant="outline"
                  size="sm"
                  disabled={pagination.current === 1}
                  onClick={() => pagination.onChange(pagination.current - 1, pagination.pageSize)}
                >
                  Previous
                </ZohoButton>
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  Page {pagination.current} of {Math.ceil(pagination.total / pagination.pageSize)}
                </span>
                <ZohoButton
                  variant="outline"
                  size="sm"
                  disabled={pagination.current >= Math.ceil(pagination.total / pagination.pageSize)}
                  onClick={() => pagination.onChange(pagination.current + 1, pagination.pageSize)}
                >
                  Next
                </ZohoButton>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Empty State */}
      {filteredData.length === 0 && !loading && (
        <div className="text-center py-12 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg">
          <div className="text-gray-500 dark:text-gray-400">
            <div className="text-lg font-medium mb-2">No data found</div>
            <div className="text-sm">Try adjusting your search or filter criteria</div>
          </div>
        </div>
      )}
    </div>
  );
}

import React from "react";
import { cn } from "@/lib/utils";
import { X, Plus, Tag as TagIcon, Hash } from "lucide-react";

interface ZohoTagProps {
  label: string;
  variant?: "default" | "primary" | "success" | "warning" | "danger" | "outline";
  size?: "sm" | "md" | "lg";
  removable?: boolean;
  onRemove?: () => void;
  icon?: React.ReactNode;
  className?: string;
  onClick?: () => void;
}

export function ZohoTag({
  label,
  variant = "default",
  size = "md",
  removable = false,
  onRemove,
  icon,
  className,
  onClick,
}: ZohoTagProps) {
  const baseClasses = "inline-flex items-center font-medium rounded-full transition-colors duration-200";

  const variantClasses = {
    default: "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300",
    primary: "bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-300",
    success: "bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-300",
    warning: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-300",
    danger: "bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300",
    outline: "bg-transparent border border-gray-300 text-gray-700 dark:border-gray-600 dark:text-gray-300",
  };

  const sizeClasses = {
    sm: "px-2 py-1 text-xs gap-1",
    md: "px-3 py-1.5 text-sm gap-1.5",
    lg: "px-4 py-2 text-base gap-2",
  };

  const handleClick = () => {
    if (onClick) {
      onClick();
    }
  };

  return (
    <span
      className={cn(
        baseClasses,
        variantClasses[variant],
        sizeClasses[size],
        onClick && "cursor-pointer hover:opacity-80",
        className
      )}
      onClick={handleClick}
      data-testid={`tag-${label.toLowerCase().replace(/\s+/g, '-')}`}
    >
      {icon && (
        <span className="flex-shrink-0">
          {icon}
        </span>
      )}

      <span className="truncate">
        {label}
      </span>

      {removable && (
        <button
          type="button"
          onClick={(e) => {
            e.stopPropagation();
            onRemove?.();
          }}
          className="flex-shrink-0 ml-1 hover:bg-black hover:bg-opacity-10 rounded-full p-0.5 transition-colors duration-200"
          data-testid={`tag-remove-${label.toLowerCase().replace(/\s+/g, '-')}`}
        >
          <X className="w-3 h-3" />
        </button>
      )}
    </span>
  );
}

// Tag Input Component
interface ZohoTagInputProps {
  tags: string[];
  onTagsChange: (tags: string[]) => void;
  placeholder?: string;
  label?: string;
  error?: string;
  required?: boolean;
  disabled?: boolean;
  maxTags?: number;
  allowDuplicates?: boolean;
  tagVariant?: "default" | "primary" | "success" | "warning" | "danger";
  className?: string;
  suggestions?: string[];
}

export function ZohoTagInput({
  tags,
  onTagsChange,
  placeholder = "Add tags...",
  label,
  error,
  required,
  disabled,
  maxTags,
  allowDuplicates = false,
  tagVariant = "default",
  className,
  suggestions = [],
}: ZohoTagInputProps) {
  const [inputValue, setInputValue] = React.useState("");
  const [showSuggestions, setShowSuggestions] = React.useState(false);
  const [focusedSuggestionIndex, setFocusedSuggestionIndex] = React.useState(-1);
  const inputRef = React.useRef<HTMLInputElement>(null);

  const filteredSuggestions = React.useMemo(() => {
    if (!inputValue || !suggestions.length) return [];
    return suggestions.filter(
      suggestion =>
        suggestion.toLowerCase().includes(inputValue.toLowerCase()) &&
        (allowDuplicates || !tags.includes(suggestion))
    );
  }, [inputValue, suggestions, tags, allowDuplicates]);

  const addTag = (tag: string) => {
    const trimmedTag = tag.trim();
    if (!trimmedTag) return;

    if (!allowDuplicates && tags.includes(trimmedTag)) return;
    if (maxTags && tags.length >= maxTags) return;

    onTagsChange([...tags, trimmedTag]);
    setInputValue("");
    setShowSuggestions(false);
    setFocusedSuggestionIndex(-1);
  };

  const removeTag = (indexToRemove: number) => {
    onTagsChange(tags.filter((_, index) => index !== indexToRemove));
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" || e.key === "Tab") {
      e.preventDefault();
      if (focusedSuggestionIndex >= 0 && filteredSuggestions[focusedSuggestionIndex]) {
        addTag(filteredSuggestions[focusedSuggestionIndex]);
      } else if (inputValue.trim()) {
        addTag(inputValue);
      }
    } else if (e.key === "Backspace" && !inputValue && tags.length > 0) {
      removeTag(tags.length - 1);
    } else if (e.key === "ArrowDown") {
      e.preventDefault();
      setFocusedSuggestionIndex(prev => 
        prev < filteredSuggestions.length - 1 ? prev + 1 : 0
      );
    } else if (e.key === "ArrowUp") {
      e.preventDefault();
      setFocusedSuggestionIndex(prev => 
        prev > 0 ? prev - 1 : filteredSuggestions.length - 1
      );
    } else if (e.key === "Escape") {
      setShowSuggestions(false);
      setFocusedSuggestionIndex(-1);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setInputValue(value);
    setShowSuggestions(value.length > 0 && filteredSuggestions.length > 0);
    setFocusedSuggestionIndex(-1);
  };

  const handleSuggestionClick = (suggestion: string) => {
    addTag(suggestion);
    inputRef.current?.focus();
  };

  return (
    <div className="w-full">
      {label && (
        <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
          {label}
          {required && <span className="text-red-500 ml-1">*</span>}
        </label>
      )}

      <div className="relative">
        <div
          className={cn(
            "min-h-[2.5rem] px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-700 focus-within:ring-2 focus-within:ring-blue-500 focus-within:border-blue-500 transition-colors duration-200 cursor-text",
            error && "border-red-500 focus-within:ring-red-500 focus-within:border-red-500",
            disabled && "opacity-50 cursor-not-allowed bg-gray-100 dark:bg-gray-800",
            className
          )}
          onClick={() => inputRef.current?.focus()}
          data-testid="tag-input-container"
        >
          <div className="flex flex-wrap items-center gap-2">
            {/* Existing Tags */}
            {tags.map((tag, index) => (
              <ZohoTag
                key={index}
                label={tag}
                variant={tagVariant}
                size="sm"
                removable={!disabled}
                onRemove={() => removeTag(index)}
              />
            ))}

            {/* Input */}
            <input
              ref={inputRef}
              type="text"
              value={inputValue}
              onChange={handleInputChange}
              onKeyDown={handleKeyDown}
              onFocus={() => {
                if (inputValue && filteredSuggestions.length > 0) {
                  setShowSuggestions(true);
                }
              }}
              onBlur={() => {
                // Delay hiding suggestions to allow click events
                setTimeout(() => setShowSuggestions(false), 150);
              }}
              placeholder={tags.length === 0 ? placeholder : ""}
              disabled={disabled || (maxTags && tags.length >= maxTags)}
              className="flex-1 min-w-[100px] bg-transparent border-0 outline-none text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
              data-testid="tag-input-field"
            />
          </div>
        </div>

        {/* Suggestions Dropdown */}
        {showSuggestions && filteredSuggestions.length > 0 && (
          <div className="absolute z-20 w-full mt-1 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-md shadow-lg max-h-40 overflow-y-auto">
            {filteredSuggestions.map((suggestion, index) => (
              <button
                key={index}
                type="button"
                onClick={() => handleSuggestionClick(suggestion)}
                className={cn(
                  "w-full px-3 py-2 text-left hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors duration-200",
                  index === focusedSuggestionIndex && "bg-blue-50 dark:bg-blue-900/20 text-blue-600 dark:text-blue-400"
                )}
                data-testid={`tag-suggestion-${index}`}
              >
                <div className="flex items-center gap-2">
                  <TagIcon className="w-4 h-4 text-gray-400" />
                  <span>{suggestion}</span>
                </div>
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Helper Text */}
      <div className="mt-1 text-sm">
        {error && (
          <div className="text-red-600 dark:text-red-400">
            {error}
          </div>
        )}
        {!error && maxTags && (
          <div className="text-gray-500 dark:text-gray-400">
            {tags.length}/{maxTags} tags
          </div>
        )}
      </div>
    </div>
  );
}

// Tag Group Component
interface ZohoTagGroupProps {
  title?: string;
  tags: Array<{
    label: string;
    value: string;
    count?: number;
    variant?: "default" | "primary" | "success" | "warning" | "danger";
  }>;
  selectedTags: string[];
  onTagToggle: (value: string) => void;
  maxVisible?: number;
  showCount?: boolean;
  className?: string;
}

export function ZohoTagGroup({
  title,
  tags,
  selectedTags,
  onTagToggle,
  maxVisible,
  showCount = false,
  className,
}: ZohoTagGroupProps) {
  const [showAll, setShowAll] = React.useState(false);

  const visibleTags = maxVisible && !showAll ? tags.slice(0, maxVisible) : tags;
  const hiddenCount = maxVisible ? Math.max(0, tags.length - maxVisible) : 0;

  return (
    <div className={cn("space-y-3", className)}>
      {title && (
        <h4 className="text-sm font-medium text-gray-900 dark:text-white">
          {title}
        </h4>
      )}

      <div className="flex flex-wrap gap-2">
        {visibleTags.map((tag) => {
          const isSelected = selectedTags.includes(tag.value);

          return (
            <ZohoTag
              key={tag.value}
              label={`${tag.label}${showCount && tag.count ? ` (${tag.count})` : ""}`}
              variant={isSelected ? (tag.variant || "primary") : "outline"}
              onClick={() => onTagToggle(tag.value)}
              className={cn(
                "cursor-pointer transition-all duration-200",
                isSelected && "ring-2 ring-blue-200 dark:ring-blue-800"
              )}
            />
          );
        })}

        {maxVisible && hiddenCount > 0 && (
          <button
            onClick={() => setShowAll(!showAll)}
            className="inline-flex items-center px-3 py-1.5 text-sm font-medium text-blue-600 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 transition-colors duration-200"
            data-testid="tag-group-show-more"
          >
            {showAll ? "Show less" : `+${hiddenCount} more`}
          </button>
        )}
      </div>
    </div>
  );
}

// Pill Component (similar to tag but more rounded)
interface ZohoPillProps {
  label: string;
  variant?: "default" | "primary" | "success" | "warning" | "danger";
  size?: "sm" | "md" | "lg";
  icon?: React.ReactNode;
  badge?: string | number;
  className?: string;
  onClick?: () => void;
}

export function ZohoPill({
  label,
  variant = "default",
  size = "md",
  icon,
  badge,
  className,
  onClick,
}: ZohoPillProps) {
  const baseClasses = "inline-flex items-center font-medium rounded-full transition-colors duration-200";

  const variantClasses = {
    default: "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300",
    primary: "bg-blue-600 text-white",
    success: "bg-green-600 text-white",
    warning: "bg-yellow-600 text-white",
    danger: "bg-red-600 text-white",
  };

  const sizeClasses = {
    sm: "px-3 py-1 text-xs gap-1.5",
    md: "px-4 py-2 text-sm gap-2",
    lg: "px-6 py-3 text-base gap-2.5",
  };

  return (
    <span
      className={cn(
        baseClasses,
        variantClasses[variant],
        sizeClasses[size],
        onClick && "cursor-pointer hover:opacity-80",
        className
      )}
      onClick={onClick}
      data-testid={`pill-${label.toLowerCase().replace(/\s+/g, '-')}`}
    >
      {icon && (
        <span className="flex-shrink-0">
          {icon}
        </span>
      )}

      <span className="truncate">
        {label}
      </span>

      {badge && (
        <span className="flex-shrink-0 ml-1 px-2 py-0.5 text-xs bg-black bg-opacity-20 rounded-full">
          {badge}
        </span>
      )}
    </span>
  );
}

import React from "react";
import { cn } from "@/lib/utils";

interface ZohoTooltipProps {
  content: string | React.ReactNode;
  children: React.ReactNode;
  position?: "top" | "bottom" | "left" | "right";
  variant?: "default" | "dark" | "light";
  delay?: number;
  disabled?: boolean;
  className?: string;
  maxWidth?: string;
}

export function ZohoTooltip({
  content,
  children,
  position = "top",
  variant = "default",
  delay = 200,
  disabled = false,
  className,
  maxWidth = "max-w-xs",
}: ZohoTooltipProps) {
  const [isVisible, setIsVisible] = React.useState(false);
  const [showTooltip, setShowTooltip] = React.useState(false);
  const timeoutRef = React.useRef<NodeJS.Timeout>();
  const tooltipRef = React.useRef<HTMLDivElement>(null);
  const triggerRef = React.useRef<HTMLDivElement>(null);

  const handleMouseEnter = () => {
    if (disabled) return;

    clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(() => {
      setIsVisible(true);
      setTimeout(() => setShowTooltip(true), 10);
    }, delay);
  };

  const handleMouseLeave = () => {
    clearTimeout(timeoutRef.current);
    setShowTooltip(false);
    setTimeout(() => setIsVisible(false), 150);
  };

  const positionClasses = {
    top: "bottom-full left-1/2 transform -translate-x-1/2 mb-2",
    bottom: "top-full left-1/2 transform -translate-x-1/2 mt-2",
    left: "right-full top-1/2 transform -translate-y-1/2 mr-2",
    right: "left-full top-1/2 transform -translate-y-1/2 ml-2",
  };

  const arrowClasses = {
    top: "top-full left-1/2 transform -translate-x-1/2 border-l-4 border-r-4 border-t-4 border-transparent",
    bottom: "bottom-full left-1/2 transform -translate-x-1/2 border-l-4 border-r-4 border-b-4 border-transparent",
    left: "left-full top-1/2 transform -translate-y-1/2 border-t-4 border-b-4 border-l-4 border-transparent",
    right: "right-full top-1/2 transform -translate-y-1/2 border-t-4 border-b-4 border-r-4 border-transparent",
  };

  const variantClasses = {
    default: "bg-gray-900 text-white border-gray-900",
    dark: "bg-black text-white border-black",
    light: "bg-white text-gray-900 border-gray-200 shadow-lg",
  };

  const arrowColors = {
    default: "border-t-gray-900",
    dark: "border-t-black",
    light: "border-t-white",
  };

  React.useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return (
    <div 
      className="relative inline-block"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      ref={triggerRef}
    >
      {children}

      {isVisible && (
        <div
          ref={tooltipRef}
          className={cn(
            "absolute z-50 px-3 py-2 text-sm rounded-md border transition-all duration-150",
            positionClasses[position],
            variantClasses[variant],
            maxWidth,
            showTooltip ? "opacity-100 scale-100" : "opacity-0 scale-95",
            className
          )}
          data-testid="tooltip"
        >
          <div className="relative">
            {content}

            {/* Arrow */}
            <div
              className={cn(
                "absolute w-0 h-0",
                arrowClasses[position],
                position === "top" && arrowColors[variant],
                position === "bottom" && `border-b-gray-900 ${variant === "light" ? "border-b-white" : variant === "dark" ? "border-b-black" : "border-b-gray-900"}`,
                position === "left" && `border-l-gray-900 ${variant === "light" ? "border-l-white" : variant === "dark" ? "border-l-black" : "border-l-gray-900"}`,
                position === "right" && `border-r-gray-900 ${variant === "light" ? "border-r-white" : variant === "dark" ? "border-r-black" : "border-r-gray-900"}`
              )}
            />
          </div>
        </div>
      )}
    </div>
  );
}

// Tooltip with Rich Content
interface ZohoRichTooltipProps {
  title?: string;
  content: React.ReactNode;
  children: React.ReactNode;
  position?: "top" | "bottom" | "left" | "right";
  width?: string;
  trigger?: "hover" | "click";
  className?: string;
}

export function ZohoRichTooltip({
  title,
  content,
  children,
  position = "top",
  width = "w-80",
  trigger = "hover",
  className,
}: ZohoRichTooltipProps) {
  const [isVisible, setIsVisible] = React.useState(false);
  const [showTooltip, setShowTooltip] = React.useState(false);

  const handleToggle = () => {
    if (trigger === "click") {
      if (isVisible) {
        setShowTooltip(false);
        setTimeout(() => setIsVisible(false), 150);
      } else {
        setIsVisible(true);
        setTimeout(() => setShowTooltip(true), 10);
      }
    }
  };

  const handleMouseEnter = () => {
    if (trigger === "hover") {
      setIsVisible(true);
      setTimeout(() => setShowTooltip(true), 10);
    }
  };

  const handleMouseLeave = () => {
    if (trigger === "hover") {
      setShowTooltip(false);
      setTimeout(() => setIsVisible(false), 150);
    }
  };

  const positionClasses = {
    top: "bottom-full left-1/2 transform -translate-x-1/2 mb-2",
    bottom: "top-full left-1/2 transform -translate-x-1/2 mt-2",
    left: "right-full top-1/2 transform -translate-y-1/2 mr-2",
    right: "left-full top-1/2 transform -translate-y-1/2 ml-2",
  };

  React.useEffect(() => {
    if (trigger === "click" && isVisible) {
      const handleClickOutside = (event: MouseEvent) => {
        const target = event.target as Element;
        if (!target.closest('[data-testid="rich-tooltip"]') && !target.closest('[data-testid="rich-tooltip-trigger"]')) {
          setShowTooltip(false);
          setTimeout(() => setIsVisible(false), 150);
        }
      };

      document.addEventListener("mousedown", handleClickOutside);
      return () => document.removeEventListener("mousedown", handleClickOutside);
    }
  }, [isVisible, trigger]);

  return (
    <div className="relative inline-block">
      <div
        onClick={handleToggle}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        data-testid="rich-tooltip-trigger"
      >
        {children}
      </div>

      {isVisible && (
        <div
          className={cn(
            "absolute z-50 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg transition-all duration-150",
            positionClasses[position],
            width,
            showTooltip ? "opacity-100 scale-100" : "opacity-0 scale-95",
            className
          )}
          data-testid="rich-tooltip"
        >
          {title && (
            <div className="px-4 py-3 border-b border-gray-200 dark:border-gray-700">
              <h4 className="font-medium text-gray-900 dark:text-white">
                {title}
              </h4>
            </div>
          )}

          <div className="p-4">
            {content}
          </div>
        </div>
      )}
    </div>
  );
}

// Help Tooltip with Icon
interface ZohoHelpTooltipProps {
  content: string | React.ReactNode;
  position?: "top" | "bottom" | "left" | "right";
